mapper.insert:
INSERT INTO location_tb
(
	location_sn
	, location_nm
	, projectid
) VALUES (
	nextval(location_seq)
	, #{location_nm}
	, #{projectid}
)

mapper.update:
UPDATE location_tb SET
	location_nm = #{location_nm}
	, projectid = #{projectid}
WHERE location_sn = #{location_sn}

mapper.select:
SELECT
	COUNT(*) AS CNT
FROM location_tb
WHERE projectid = #{projectid}
AND location_nm = #{location_nm}
