mapper.select:
SELECT * FROM sensor_tb
sensorid LIKE CONCAT('%', #{search.keyword}, '%')
ORDER BY sensorid, channel ASC LIMIT ${amount} OFFSET ${(pageNum-1)*amount};

SELECT COUNT(*) FROM sensor_tb
sensorid LIKE CONCAT('%', #{search.keyword}, '%')
ORDER BY sensorid, channel ASC LIMIT ${amount} OFFSET ${(pageNum-1)*amount};

SELECT sensorid , channel , IFNULL(sensorname, 'default') as sensorname , initdate , factor , guidel1max , guidel1min , guidel2max , guidel2min , guidel3max , guidel3min , const1 , const2 , const3 , const4 , const5 , const6 , const7 , const8 , calcstring , projectid FROM sensor_tb
AND sensorid = #{sensorid}
AND channel = #{channel}
AND projectid = #{projectid}

SELECT
	TB.keytime as time
	, AVG(TB.aiv) as aiv
FROM (
	SELECT
		date_format(time, '%Y-%m-%d %H:00') as keytime
		,time
		,aiv
	FROM sensor_report
	WHERE sensorid = #{sensorid}
	AND channel = #{channel}
	AND time >= #{sdatetime}
	AND time <= #{edatetime}
) TB
GROUP BY TB.keytime
ORDER BY TB.keytime ASC

mapper.insert:
	INSERT INTO sensor_tb
	(
            sensorid
            , channel
            , sensorname
            , initdate
            , factor
            , guidel1max
            , guidel1min
            , guidel2max
            , guidel2min
            , guidel3max
            , guidel3min
            , const1
            , const2
            , const3
            , const4
            , const5
            , const6
            , const7
            , const8
			, calcstring
			, projectid
			, location_sn

	)
	values (
            #{sensorid}
            , #{channel}
            , #{sensorname}
            , #{initdate}
            , #{factor}
            , #{guidel1max}
            , #{guidel1min}
            , #{guidel2max}
            , #{guidel2min}
            , #{guidel3max}
            , #{guidel3min}
            , #{const1}
            , #{const2}
            , #{const3}
            , #{const4}
            , #{const5}
            , #{const6}
            , #{const7}
            , #{const8}
            , #{calcstring}
	        , #{projectid}
			, #{location_sn}
	)

mapper.update:
	UPDATE sensor_tb SET
              sensorname      = #{sensorname}
            , initdate      = #{initdate}
            , factor        = #{factor}
            , guidel1max    = #{guidel1max}
            , guidel1min    = #{guidel1min}
            , guidel2max    = #{guidel2max}
            , guidel2min    = #{guidel2min}
            , guidel3max    = #{guidel3max}
            , guidel3min    = #{guidel3min}
            , const1        = #{const1}
            , const2        = #{const2}
            , const3        = #{const3}
            , const4        = #{const4}
            , const5        = #{const5}
            , const6        = #{const6}
            , const7        = #{const7}
            , const8        = #{const8}
            , userid        = #{userid}
			, calcstring	= #{calcstring}
	WHERE	projectid		= #{projectid}
	  AND	sensorid		= #{sensorid}
	  AND	channel			= #{channel}

mapper.delete:
DELETE FROM sensor_tb
 WHERE	projectid = #{projectid}
   AND	sensorid = #{sensorid}
   AND	channel = #{channel}
